import React, {useStateS} from "react";
import App from "../../App";

const CartContext = React.createContext();

export const CartProvider = ( {children} ) => {

    const [cartItems, setCartItems] = useState([]);

    const addCartItem = () => {
        setCartItems([...cartItems], {title: cartItems.length+1});
    }

    const [Appetizer, setAppetizers] = useState([ 
        {id: 100, Category: "Appetizer", title: "Hummus", description: "pureed chick peas tahini with olive oil", imageSource: require('../../assets/Hummus.jpg'), price: 7},
