        {id: 106, Category: "Appetizer", title:"Fava Foul", description:"muddled fava beans and jalapeno sauce", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 8},
        {id: 107, Category: "Appetizer", title:"Musabaha", description:"whole chick peas, hummus, and jalapeno sauce with olive oil",imageSource: require('../../assets/KabobHouseLogo.jpg'),price: 8},
        {id: 108, Category: "Appetizer", title:"Meat Pie", description:"spicy ground beef and veggies on bread(2pc)",imageSource: require('../../assets/meatpie.jpg'), price: 6},
        {id: 109, Category: "Appetizer", title:"Zaatar Pie", description:"earthy spice blend with olive oil on bread(2pc)",imageSource: require('../../assets/ZaatarPie.jpg'), price: 5},
        {id: 110, Category: "Appetizer", title:"Spinach Pyramid", description:"spinach and spiced onions in bread(2pc)", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 6},
        {id: 111, Category: "Appetizer", title:"Cheese and Herb Pie", description:"crumble cheese mix and fresh herbs in bread(2pc)",imageSource: require('../../assets/CheesePie.jpg'), price: 6},
        {id: 112, Category: "Appetizer", title:"Shawarma Hummus Chicken", description:"Hummus with Chicken Shawarma on top",imageSource: require('../../assets/chickenhummus.jpg'), price: 1},
        {id: 113, Category: "Appetizer", title:"Shawarma Hummus Beef", description:"Hummus with Beef Shawarma on top", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 15},
    ]);
    const Drink=[
        {id: 114, Category: "Drink", title: "Lemonade Mint Slush",  description: "blended lemonade, ice, and mint", imageSource: require('../../assets/mintlemonade.jpg'), price: 4},
        {id: 115, Category: "Drink", title: "Lebaneze Iced Tea", description: "Sweet tea with rose water", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 3},
        {id: 116, Category: "Drink", title: "Water", description: "Water bottle(ozarka)", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 1.50},
        {id: 117, Category: "Drink", title: "Fountain Drink",description: "20oz fountain drink",imageSource:require('../../assets/KabobHouseLogo.jpg'), price: 2.75},
        {id: 118, Category: "Drink", title: "Yogurt drink",description: "Ayran-Shaneenah-yogurt drink", imageSource: require('../../assets/yogurtdrink.jpg'), price:4},
    ];
    const Plate = [
        {id: 119, Category: "Plate", title:"Chicken Shawarma Plate",description: "shave cut marinated Chicken", imageSource: require('../../assets/Chickenshawplate.jpg'), price: 17},
        {id: 120, Category: "Plate", title:"Beef Shawarma Plate", description: "tender thin cut Beef",imageSource: require('../../assets/beefplate.jpg'), price: 18},
        {id: 121, Category: "Plate", title:"Gyro Plate", description: "Lamb and Beef thin cut mix", imageSource: require('../../assets/gyroplate.jpg'), price: 17},
