        {id: 131, Category: "Plate", title:"Veggie Sauteed Plate", description: "Sauteed onions, peppers, zucchini, and carrots", imageSource:require('../../assets/KabobHouseLogo.jpg'), price: 12},
    ];
    const Salad=[
        {id: 132, Category: "Salad", title: "Greek Salad",  description: "Romaine lettuce, cucumber, onion, olives, tomatoes, bell peppers, feta cheese dressed with lemon, salt, and olive oil dressing",imageSource: require('../../assets/greeksalad.jpg'), price: 10},
        {id: 133, Category: "Salad", title: "Arabic Salad", description: "Diced cucumbers, tomato, green onion, parsley dressed with lemon, salt, and olive oil dressing", imageSource: require('../../assets/arabicsalad.jpg'),price: 8},
        {id: 134, Category: "Salad", title: "Tabouli",      description: "fine chopped parsley, cracked wheat, tomatoes, and cucumber dressed with lemon salt and olive oil dressing", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 8},
        {id: 135, Category: "Salad", title:"KH Special Salad", description: "Romaine lettuce, cucumbers, onions, tomatoes, onions, apples, mint, bell peppers, sunflower seeds, and zaatar dressed with lemon, salt, and olive oil dressing", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 12},
        {id: 136, Category: "Salad", title: "Lentil Soup", description: "Flavorful lentil, carrot, and onion pureed soup(Cup 8oz)", imageSource: require('../../assets/KabobHouseLogo.jpg'),price: 5},
        {id: 137, Category: "Salad", title: "Lentil Soup", description: "Flavorful lentil, carrot, and onion pureed soup(Bowl 12oz)", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 7},
    ];


    const Wrap=[
        {id: 138, Category: "Wrap", title: "Chicken Shawarma Wrap",  description: "Garlic and herb Chicken Shawarma wrap dressed chefs choice served with seasoned fries", imageSource: require('../../assets/chickenwrap.jpg'), price: 10},
        {id: 139, Category: "Wrap", title: "Falafel Wrap", description: "Garlic and herb Falafel wrap dressed chefs choice served with seasoned fries", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 10},
        {id: 140, Category: "Wrap", title: "Beef Shawarma Wrap", description: "Garlic and herb Beef Shawarma wrap dressed chefs choice served with seasoned fries", imageSource: require('../../assets/beefwrap.jpg'), price: 10},
        {id: 141, Category: "Wrap", title: "Gyro Wrap",description: "Garlic and herb Gyro wrap dressed chefs choice served with seasoned fries",imageSource:require('../../assets/GyroWrap.jpg'), price: 10},
        {id: 142, Category: "Wrap", title: "Kufta Kabob Wrap",description: "Garlic and herb Kufta Kabob wrap dressed chefs choice served with seasoned fries", imageSource: require('../../assets/KabobHouseLogo.jpg'), price:11},
        {id: 143, Category: "Wrap", title: "Lamb Kabob Wrap",description: "Garlic and herb Lamb Kabob wrap dressed chefs choice served with seasoned fries", imageSource: require('../../assets/KabobHouseLogo.jpg'), price:11},
        {id: 144, Category: "Wrap", title: "Shrimp Wrap",description: "Garlic and herb Shrimp Wrap dressed chefs choice served with seasoned fries", imageSource: require('../../assets/KabobHouseLogo.jpg'), price:11},
        {id: 145, Category: "Wrap", title: "Veggie Wrap",description: "Garlic and herb sautteed veggies wrap dressed chefs choice served with seasoned fries", imageSource: require('../../assets/KabobHouseLogo.jpg'), price:11},

    ];


    return (<CartContext.Provider value={{ data: cartItems, addCartItem: addCartItem }}>

            {children}

    </CartContext.Provider>
    )};

export default CartContext;
