import React, {useContext} from "react";
import {Text, StyleSheet, Button, View, TouchableOpacity, Image, ScrollView, FlatList} from 'react-native';

import ItemDetail from "../components/ItemDetail";
import CartContext from "../context/CartContext";
import InnerMenuContext from "../context/InnerMenuContext"

const InnerMenu = () => {
    const{state, loadDetails} = useContext(InnerMenuContext);
    return (
        <View style={styles.backGround}>
            <ScrollView>
                <View>
                <FlatList
                        data={state}
                        keyExtractor={(item) => {return item.title}}
                        renderItem={({ item }) => (

                            <ItemDetail title={item.title} imageSource={item.imageSource} price={item.price} description={item.description}/>

                        )}/>
                    <View style={styles.viewStyle}>
                    <Text>{"\n\n\n"}</Text>
                    {/* <Text style={styles.title}>{MenuItems.title} {"\n"}</Text>
                    <Image style={styles.image} source={MenuItems.imageSource}/>
                        <View style={styles.viewStyle1}>
                        <Text>{"\n"}</Text>

                        <Text style={styles.textStyle}>{MenuItems.description} {"\n"}</Text>
                        <Text style={styles.textStyle}> ${MenuItems.price}</Text>     */}
                        </View>
                        {/* <Button onPress={onAddToCart()}
                            title="Add Item to Cart"
                        /> */}


                </View>
            </ScrollView>
        </View>
    );
}

const styles = StyleSheet.create({
    backGround:{
        backgroundColor: '#ffffff',
        overflow: 'scroll'
    },
    container1:{
        alignContent: "space-between"
    },
    container:{

        alignItems: "center"
    },
    viewStyle:{
        flexDirection: "column",
          alignContent: "space-around",
        alignItems: "center", 
      },
      viewStyle1:{
          flexDirection: "column",
          alignContent: "space-around",
          alignItems:"center",
      },
      image:{
          width : 150,
          height: 150,

      },
      title:{
          fontWeight: "bold",
          fontSize: 25,
      },
      textStyle:{
          flexDirection: "row",
          flexShrink: 1,

          fontSize:18,


      },
    subtitle: {

            textAlign: "center",
            alignContent: "space-around",
            fontSize: 15,
            fontWeight: "bold",

    },
    logostyle:{

        width : 150,
        height: 150,
    },
    BottomNav: {
        flexDirection:"row",
        backgroundColor: "red",
        justifyContent:"center",
        alignContent: "space-around"

    }
});

export default InnerMenu;
