import React, { useState, useContext } from "react";
import {Text, StyleSheet, View, FlatList,Button, TouchableOpacity, Image, ScrollView} from "react-native";
import ImageDetail from "../components/ImageDetail";
import CartContext from "../context/CartContext";



// var Appetizer = {Hummus: ["Hummus", "pureed chick peas tahini with olive oil", require('../../assets/Hummus.jpg'), price: 7},
//     BabaGhanoush:["Baba Ghanoush", "pureed eggplant tahini with olive oil",imageSource: require('../../assets/babaghanoush.jpg'), price: 7},
//     Labneh: ["Labneh", "tangy creamed greek yogurt with olive oil",imageSource: require('../../assets/Labneh.jpg'), price: 6},
//     BreakfastCombo: ["Breakfast Combo", "hummus, labneh, baba ghanoush, and falafel", 15],
//     GrapeLeaves: ["Grape Leaves", "grape leaves stuffed with veggies and rice (8pc)", 8],
//     Falafel: ["Falafel", "deep fried chick peas and herbs bites(6pc)",imageSource: require('../../assets/falafel.jpg'), price: 5},
//     FavaFoul: ["Fava Foul", "muddled fava beans and jalapeno sauce", 8],
//     Musabaha: ["Musabaha", "whole chick peas, hummus, and jalapeno sauce with olive oil", 8],
//     MeatPie: ["Meat Pie", "spicy ground beef and veggies on bread(2pc)",imageSource: require('../../assets/meatpie.jpg'), price: 6},
//     ZaatarPie: ["Zaatar Pie", "earthy spice blend with olive oil on bread(2pc)",imageSource: require('../../assets/ZaatarPie.jpg'), price: 5},
//     SpinachPyramid: ["Spinach Pyramid", "spinach and spiced onions in bread(2pc)", 6],
//     CheesePie:["Cheese and Herb Pie", "crumble cheese mix and fresh herbs in bread(2pc)",imageSource: require('../../assets/CheesePie.jpg'), price: 6},
//     ChickenHummus: ["Shawarma Hummus Chicken", "Hummus with Chicken Shawarma on top",imageSource: require('../../assets/chickenhummus.jpg'), price: 1}],
//     BeefHummus: ["Shawarma Hummus Beef", "Hummus with Beef Shawarma on top", 15]};
//complete uprooting of the data to entertain state into our project :)

//Use dictionary to populate each imagedetail component
//0 is title, 1 is description, 2 is source, 3 is price
const Appetizers = (props) => {
    const[Appetizer, setPrices] =useState([ 
        {id: 100, title: "Hummus", description: "pureed chick peas tahini with olive oil", imageSource: require('../../assets/Hummus.jpg'), price: 7},
        {id: 101, title: "Baba Ghanoush", description: "pureed eggplant tahini with olive oil",imageSource: require('../../assets/babaghanoush.jpg'), price: 7},
        {id: 102, title:"Labneh", description:"tangy creamed greek yogurt with olive oil",imageSource: require('../../assets/Labneh.jpg'), price: 6},
        {id: 103, title:"Breakfast Combo", description:"hummus, labneh, baba ghanoush, and falafel",imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 15},
        {id: 104, title:"Grape Leaves", description:"grape leaves stuffed with veggies and rice (8pc)",imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 8},
        {id: 105, title:"Falafel", description:"deep fried chick peas and herbs bites(6pc)",imageSource: require('../../assets/falafel.jpg'), price: 5},
        {id: 106, title:"Fava Foul", description:"muddled fava beans and jalapeno sauce", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 8},
        {id: 107, title:"Musabaha", description:"whole chick peas, hummus, and jalapeno sauce with olive oil",imageSource: require('../../assets/KabobHouseLogo.jpg'),price: 8},
        {id: 108, title:"Meat Pie", description:"spicy ground beef and veggies on bread(2pc)",imageSource: require('../../assets/meatpie.jpg'), price: 6},
        {id: 109, title:"Zaatar Pie", description:"earthy spice blend with olive oil on bread(2pc)",imageSource: require('../../assets/ZaatarPie.jpg'), price: 5},
        {id: 110, title:"Spinach Pyramid", description:"spinach and spiced onions in bread(2pc)", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 6},
        {id: 111, title:"Cheese and Herb Pie", description:"crumble cheese mix and fresh herbs in bread(2pc)",imageSource: require('../../assets/CheesePie.jpg'), price: 6},
        {id: 112, title:"Shawarma Hummus Chicken", description:"Hummus with Chicken Shawarma on top",imageSource: require('../../assets/chickenhummus.jpg'), price: 1},
        {id: 113, title:"Shawarma Hummus Beef", description:"Hummus with Beef Shawarma on top", imageSource: require('../../assets/KabobHouseLogo.jpg'), price: 15}
    ]);
    const appetizers = useContext(CartContext);
    return (
        <View>
            <ScrollView>
                <View style={styles.backGround}>
                    <View style={styles.container}>
                        <Image style={styles.logostyle} source={require('../../assets/KabobHouseLogo.jpg')}/>
                    </View>
                    <Text style={styles.title}>Appetizers{"\n"}</Text>     
                    <FlatList
                        data={Appetizer}
                        renderItem={({ item }) => (
                            <TouchableOpacity onPress={() => props.navigation.navigate('InnerMenu', item)}>
                                <ImageDetail title={item.title} imageSource={item.imageSource}/>
                                {/* <Text>{item.title}</Text> */}
                            </TouchableOpacity>
                        )}
                        />

                    <View style={styles.BottomNav}>
                        <TouchableOpacity onPress={() => {props.navigation.navigate("Home")}}>
                            <Text>HOME</Text> 
                        </TouchableOpacity>
                        <TouchableOpacity onPress={() => {props.navigation.navigate("Phone")}}>
                            <Text>Phone</Text> 
                        </TouchableOpacity>
                        <TouchableOpacity onPress={() => {props.navigation.navigate("Menu")}}>
                            <Text>Menu</Text> 
                        </TouchableOpacity>
                        <TouchableOpacity onPress={() => {props.navigation.navigate("Map")}}>
                            <Text>Map</Text> 
                        </TouchableOpacity>

                    </View>
                </View>
            </ScrollView>
        </View>

    );
}

const styles = StyleSheet.create({
    backGround:{
        backgroundColor: '#ffffff',
        overflow: 'scroll'
    },
    container:{

        alignItems: "center"
    },
    title:{
        textAlign: "center",
        alignContent: "space-around",
        fontSize: 20,
        fontWeight: "bold",
    },
    logostyle:{

        width : 150,
        height: 150,
    },
    BottomNav: {
        flexDirection:"row",
        backgroundColor: "red",
        justifyContent:"center",
        alignContent: "space-around"

    }
});

export default Appetizers;
