import React, {useReducer, useState} from "react";
import createDataContext from "./createDataContext";


const cartReducer = (state, action) => {
    switch(action.type) {
        case 'add_cartitem':
            return [...state, {id: Math.floor(Math.random() *9999999), title: `Item #${state.length+1}`}]
        default:
            return state;
    }

}


    const addCartItem = (dispatch) => {
        return() => {
            dispatch({type: 'add_cartitem'});
        }

    }

export const {Context, Provider} = createDataContext(cartReducer, {addCartItem:addCartItem}, []);
